import React, { Component } from 'react';
import PostModal from './PostModal';
import './CreatePostHover.css';

class CreatePostHover extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modalShow: true
        }
    }

    postModalCall = () => {
        this.setState({ modalShow: !this.state.modalShow });
    }

    render() {
        return (
            <div>
                {/* { this.state.modalShow ? <PostModal author='subhro'/>: null } */}
                <PostModal author="subhro"/>
                <button className="create-post-hover-button" onClick={this.postModalCall}>Create New Post</button>
            </div>
        )
    }    
}

export default CreatePostHover;
