import React, { Component } from 'react';
import PostModal from './PostModal';
import './CreatePostHover.css';

class CreatePostHover extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modalShow: false
        }
    }

    postModalCall = () => {
        this.setState({ modalShow: !this.state.modalShow });
    }

    render() {
        return (
            <div>
                { this.state.modalShow ? 
                        <PostModal author='subhro' onCall={this.postModalCall}/> : null }
                {/* <PostModal author="subhro"/> */}
                <button 
                    className="create-post-hover-button" 
                    onClick={this.postModalCall}
                    disabled={this.state.modalShow}
                >Create New Post</button>
            </div>
        )
    }    
}

export default CreatePostHover;
