import React, { Component } from 'react';

class PostModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            postbody: ''
        }
    }

    onModalClose = (e) => {
        console.log(this.props);
        if(this.state.title != '' && this.state.postbody != '') {
            if(window.confirm('Are you sure to close ? Data will be deleted')) {
                this.props.onCall();
            }
        } else{
            this.props.onCall();
        } 
    }

    onTitleChange = (e) => this.setState({ title: e.target.value });
    onBodyChange = (e) => this.setState({ postbody: e.target.value });

    render() {
        return (
            <div className="modal-container">
                <div className="modal-header">
                    <h3>Create a new post</h3>
                    <span className="close" onClick={this.onModalClose}>&time;</span>
                </div>
                <div className="modal-body">
                    <form action="">
                        <input type="text" name="title" value={this.props.author} disabled/>
                        <input type="text" name="title" onChange={this.onTitleChange}/>
                        <button>Uploaad image - feature coming soon</button>
                        <textarea name="postbody" onChange={this.onBodyChange}/>
                        <button type="submit">Create</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default PostModal;
