import React, { Component } from 'react';
import './PostModal.css';
class PostModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            postbody: ''
        }
    }

    backToPost = (e) => {
        console.log(this.props);
        if(this.state.title != '' && this.state.postbody != '') {
            if(window.confirm('Are you sure to close ? Data will be deleted')) {
                this.props.onCall();
            }
        } else{
            this.props.onCall();
        } 
    }

    onTitleChange = (e) => this.setState({ title: e.target.value });
    onBodyChange = (e) => this.setState({ postbody: e.target.value });

    render() {
        return (
            <div className="create-post-modal-container">
                <div className="post-container">
                    <span className="close" onClick={this.backToPost}>&#8592;</span>
                    <div className="post-header">
                        <h3>Create a new post</h3>
                    </div>
                    <div className="post-body">
                        <form action="" onSubmit={this.onSubmitForm}>
                            <input type="text" name="author" value={this.props.author} disabled/>
                            <input type="text" name="title" onChange={this.onTitleChange}/>
                            <button>Uploaad image - feature coming soon</button>
                            <textarea name="postbody" onChange={this.onBodyChange}/>
                            <button type="submit">Create</button>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default PostModal;
