import React, { Component } from 'react';
import PostModal from './PostModal';
import './CreatePostHover.css';

class CreatePostHover extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modalShow: false
        }
    }

    onCall = () => {
        this.setState({ modalShow: !this.state.modalShow });
    }

    render() {
        return (
            <div>
                { this.state.modalShow ? null : <PostModal author='subhro'/> }
                {/* <PostModal author="subhro"/> */}
                <button className="create-post-hover-button" onCall={this.postModalCall}>Create New Post</button>
            </div>
        )
    }    
}

export default CreatePostHover;
